script:
frappe.ui.form.on('Item', {
	// remember previous group to detect changes
	onload: function(frm) {
		frm._previous_item_group = frm.doc.item_group || null;
	},

	item_group: function(frm) {
		// if no group selected => clear reserved code and make editable
		if (!frm.doc.item_group) {
			// If the current item_code appears to have been reserved (we set it readonly),
			// clear it so user can enter a manual code
			frm.set_value('item_code', '');
			frm.set_df_property('item_code', 'read_only', 0);
			frm.refresh_field('item_code');
			frm._previous_item_group = null;
			return;
		}

		// If group changed from previous value, decide if item_name should be updated
		const prev_group = frm._previous_item_group;
		const group_changed = prev_group && prev_group !== frm.doc.item_group;

		// Determine whether item_name is auto-generated (best-effort):
		// - If empty OR starts with previous group name OR matches pattern "GROUP - CODE"
		let should_update_name = false;
		const current_name = frm.doc.item_name || '';
		if (!current_name) {
			should_update_name = true;
		} else if (group_changed) {
			// common auto pattern: "<prev_group> - <something>"
			if (prev_group && (current_name.startsWith(prev_group + ' ') || current_name.startsWith(prev_group + '-'))) {
				should_update_name = true;
			}
			// or pattern "SOMEGROUP - CODE"
			if (/^[\w\s]+ - \w+/.test(current_name)) {
				// weak heuristic — still mark as auto
				should_update_name = true;
			}
		}

		// Clear old reserved code if group changed (so we don't keep wrong group code)
		if (group_changed) {
			// only clear if it was readonly (i.e., likely reserved)
			const was_readonly = frm.get_field('item_code') && frm.get_field('item_code').df.read_only;
			if (was_readonly) {
				frm.set_value('item_code', '');
				frm.refresh_field('item_code');
				frm.set_df_property('item_code', 'read_only', 0);
			}
		}

		// Reserve a new item code from server
		frappe.call({
			method: "devp_custom.api.reserve_item_code",
			args: {
				item_group: frm.doc.item_group,
				digits: 3
			},
			freeze: true,
			freeze_message: __("Reserving item code..."),
			callback: function(r) {
				if (r.message) {
					// set the new reserved code and lock it
					frm.set_value('item_code', r.message);
					frm.set_df_property('item_code', 'read_only', 1);
					frm.refresh_field('item_code');

					// optionally update item_name if the heuristic decided to auto-update
					if (should_update_name) {
						// simple auto-name: "ItemGroup - CODE"
						const new_name = frm.doc.item_group + " - " + r.message;
						frm.set_value('item_name', new_name);
						frm.refresh_field('item_name');
					}
				} else {
					frappe.msgprint(__('Could not reserve item code. Try again.'));
				}
			},
			error: function() {
				frappe.msgprint(__('Could not reserve item code. Try again.'));
			}
		});

		// store current group for future change detection
		frm._previous_item_group = frm.doc.item_group;
	},

	// Safety: if item_code is still empty at validate, try reserving again
	validate: function(frm) {
		if (!frm.doc.item_code && frm.doc.item_group) {
			frappe.dom.freeze(__('Reserving item code...'));
			frappe.call({
				method: "devp_custom.api.reserve_item_code",
				args: {
					item_group: frm.doc.item_group,
					digits: 3
				},
				callback: function(r) {
					frappe.dom.unfreeze();
					if (r.message) {
						frm.set_value('item_code', r.message);
						frm.set_df_property('item_code', 'read_only', 1);
						frm.refresh_field('item_code');

						// if item_name empty, set it too
						if (!frm.doc.item_name) {
							frm.set_value('item_name', frm.doc.item_group + " - " + r.message);
							frm.refresh_field('item_name');
						}
					} else {
						frappe.msgprint(__('Could not reserve item code. Try again.'));
					}
				}
			});
		}
	}
});

// Client Script (Sales Invoice) - qty / rate / amount sync with manual_amount fallback
frappe.ui.form.on('Sales Invoice Item', {
    // On load of the parent form, ensure amount field is editable if manual_amount is absent
    form_render: function(frm) {
        // if manual_amount not present on child doctype, make built-in amount editable on client
        const df = frappe.meta.get_docfield('Sales Invoice Item', 'manual_amount', frm.doc.name);
        if (!df) {
            // Make amount editable in client only (affects current user's UI)
            try {
                frappe.model.set_df_property('Sales Invoice Item', 'amount', 'read_only', 0);
            } catch (e) {
                console.warn('Could not toggle amount read_only property:', e);
            }
        }
    },

    qty: function(frm, cdt, cdn) {
        update_row_values(frm, cdt, cdn, 'qty');
    },
    rate: function(frm, cdt, cdn) {
        update_row_values(frm, cdt, cdn, 'rate');
    },
    amount: function(frm, cdt, cdn) {
        update_row_values(frm, cdt, cdn, 'amount');
    },
    manual_amount: function(frm, cdt, cdn) {
        // if manual_amount exists and user edits it directly
        update_row_values(frm, cdt, cdn, 'manual_amount');
    },

    // On save/cancel ensure amount finalization: copy manual_amount -> amount if present
    before_save: function(frm) {
        (frm.doc.items || []).forEach(function(row) {
            if (row.manual_amount !== undefined && row.manual_amount !== null) {
                // copy manual_amount into amount so server side gets the updated value
                frappe.model.set_value(row.doctype, row.name, 'amount', row.manual_amount);
            }
        });
    }
});

function update_row_values(frm, cdt, cdn, changed_field) {
    const row = locals[cdt][cdn];
    if (!row) return;

    if (row._updating) return;
    row._updating = true;

    try {
        // Determine which "amount" field to use: manual_amount (if present) else amount
        const uses_manual = (row.manual_amount !== undefined);
        const amount_field = uses_manual ? 'manual_amount' : 'amount';

        const qty = parseFloat(row.qty) || 0;
        const rate = parseFloat(row.rate) || 0;
        const amount = parseFloat(row[amount_field]) || 0;

        // currency precision - fallback to 2
        let precision = 2;
        try {
            // try reading currency precision if available in defaults (may not always work)
            const p = frappe.defaults.get_default("currency_precision");
            if (p) precision = parseInt(p);
        } catch (e) { /* ignore */ }

        const round = (v) => {
            const p = Math.pow(10, precision);
            return Math.round((v + 0.0000000001) * p) / p;
        };

        // Logic: keep qty & rate & amount in sync based on what changed
        if (changed_field === 'qty') {
            if (qty && (row.rate !== undefined && row.rate !== null && row.rate !== '')) {
                const newAmount = round(qty * rate);
                if (newAmount !== amount) {
                    frappe.model.set_value(cdt, cdn, amount_field, newAmount);
                }
            } else if (qty && (row[amount_field] !== undefined && row[amount_field] !== null && row[amount_field] !== '')) {
                const newRate = qty > 0 ? round(row[amount_field] / qty) : 0;
                if (newRate !== rate) {
                    frappe.model.set_value(cdt, cdn, 'rate', newRate);
                }
            }
        } else if (changed_field === 'rate') {
            if (qty) {
                const newAmount = round(qty * rate);
                if (newAmount !== amount) {
                    frappe.model.set_value(cdt, cdn, amount_field, newAmount);
                }
            }
        } else if (changed_field === 'amount' || changed_field === 'manual_amount') {
            // amount changed -> recalc rate if qty present
            const newAmountVal = parseFloat(row[amount_field]) || 0;
            if (qty > 0) {
                const newRate = round(newAmountVal / qty);
                if (newRate !== rate) {
                    frappe.model.set_value(cdt, cdn, 'rate', newRate);
                }
            }
        }

        // trigger parent calculation (if present)
        try {
            frm.trigger('calculate_taxes_and_totals');
        } catch (e) {
            // ignore if parent has different calculation method
        }

    } finally {
        // small delay to ensure set_value propagation finishes, then clear flag and refresh
        setTimeout(() => {
            if (locals[cdt] && locals[cdt][cdn]) {
                locals[cdt][cdn]._updating = false;
            }
            // refresh only the child table to update values visually
            frm.refresh_field('items');
        }, 30);
    }
}

frappe.ui.form.on('Sales Invoice Item', {
    item_code: function(frm, cdt, cdn) {
        const item = locals[cdt][cdn];
        if (!item || !item.item_code) return;

        // optional: require customer on parent Sales Invoice
        const customer = frm.doc.customer || null;

        // call server method
        frappe.call({
            method: 'devp_custom.api.get_last_item_prices', // path in your app
            args: {
                item_code: item.item_code,
                customer: customer,
                limit: 5
            },
            freeze: true,
            freeze_message: 'Fetching last selling prices...',
            callback: function(r) {
                const data = (r && r.message) ? r.message : [];
                if (!data.length) {
                    frappe.msgprint({ title: 'No history', message: `No sales history found for ${item.item_code}`, indicator: 'orange' });
                    return;
                }
                show_price_dialog(frm, item, data);
            },
            error: function(err) {
                console.error('Error fetching last prices', err);
                frappe.msgprint({ title: 'Error', message: 'Failed to fetch last prices. See console.' });
            }
        });
    }
});

function show_price_dialog(frm, item, data) {
    // data expected: [{ invoice: 'SINV-...', posting_date: 'YYYY-MM-DD', rate: 123.45 }, ...]
    const fields = [{
        fieldname: 'prices',
        fieldtype: 'Table',
        cannot_add_rows: true,
        cannot_delete_rows: true,
        in_place_edit: false,
        fields: [
            {
                label: 'Sales Invoice',
                fieldname: 'invoice',
                fieldtype: 'Link',
                options: 'Sales Invoice',
                in_list_view: 1,
                read_only: 1
            },
            {
                label: 'Date',
                fieldname: 'posting_date',
                fieldtype: 'Date',
                in_list_view: 1,
                read_only: 1
            },
            {
                label: 'Rate',
                fieldname: 'rate',
                fieldtype: 'Currency',
                in_list_view: 1,
                read_only: 1
            }
        ],
        data: data
    }];

    const dialog = new frappe.ui.Dialog({
        title: `${item.item_code} — Last Selling Prices`,
        size: 'large',
        fields: fields,
        primary_action_label: 'Select',
        primary_action: function() {
            try {
                const selected = dialog.fields_dict.prices.grid.get_selected_children();
                if (!selected || !selected.length) {
                    frappe.msgprint('Please select one row to pick the rate.');
                    return;
                }
                if (selected.length > 1) {
                    frappe.msgprint('Please select only one row.');
                    return;
                }
                const chosen = selected[0];

                // set the rate on the Sales Invoice Item row
                frappe.model.set_value(item.doctype, item.name, 'rate', chosen.rate);
                dialog.hide();
            } catch (e) {
                console.error('Dialog select error', e);
                frappe.msgprint('Could not set rate — see console.');
            }
        }
    });

    dialog.show();

    // auto-select first row for convenience
    try {
        const grid = dialog.fields_dict.prices.grid;
        if (grid && grid.grid_rows && grid.grid_rows.length) {
            const firstRow = grid.grid_rows[0];
            if (firstRow && firstRow.row_select_checkbox) {
                firstRow.row_select_checkbox.click();
                grid.wrapper && grid.wrapper.scrollTop(0);
            }
        }
    } catch (e) {
        console.warn('Auto-select failed', e);
    }
}
